window.countFPS = (function () {
  var lastLoop = (new Date()).getMilliseconds();
  var count = 1;
  var fps = 0;

  return function () {
    var currentLoop = (new Date()).getMilliseconds();
    if (lastLoop > currentLoop) {
      fps = count;
      count = 1;
    } else {
      count += 1;
    }
    lastLoop = currentLoop;
    return fps;
  };
}());

window.countPI = (function () {
  var lastLoop = (new Date()).getMilliseconds();
  var count = 1;
  var packet = 0;

  return function () {
    var currentLoop = (new Date()).getMilliseconds();
    if (lastLoop > currentLoop) {
      packet = count;
      count = 1;
    } else {
      count += 1;
    }
    lastLoop = currentLoop;
    return packet;
  };
}());

